package dev.algos.snatch.interview_problems.pattern_matching;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

class LongestDuplicatedSubstringTest {

    @Test
    void test() {
        LongestDuplicatedSubstring lds = new LongestDuplicatedSubstring();
        assertEquals("ana", lds.longestDupSubstring("banana"));
        assertEquals("akyj", lds.longestDupSubstring("\"\""));
    }
}
